import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import api from '../services/api';

function Home() {
  const [items, setItems] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [bidAmounts, setBidAmounts] = useState({});

  const handleBidChange = (itemId, amount) => {
    setBidAmounts(prev => ({
      ...prev,
      [itemId]: amount
    }));
  };

  const handlePlaceBid = async (itemId, currentBid) => {
    const bidValue = parseFloat(bidAmounts[itemId] || '');

    if (!bidAmounts[itemId] || isNaN(bidValue)) {
      setError('Please enter a valid bid amount');
      return;
    }

    if (bidValue <= currentBid) {
      setError(`Bid must be higher than $${currentBid}`);
      return;
    }

    try {
      const response = await api.post(`/items/${itemId}/bid`, { amount: bidValue });
      if (response.data) {
        setItems(prevItems => prevItems.map(item => 
          item._id === itemId ? { ...item, currentBid: bidValue } : item
        ));
        setError(null);
      }
    } catch (err) {
      console.error('Error placing bid:', err);
      setError(err.response?.data?.msg || 'Failed to place bid');
    }
  };

  useEffect(() => {
    const fetchItems = async () => {
      try {
        const response = await api.getItems();
        if (!response.data) {
          throw new Error('No data received from API');
        }
        setItems(response.data);
        setError(null);
      } catch (error) {
        console.error('API Error:', {
          message: error.message,
          stack: error.stack,
          response: error.response?.data
        });
        setError(`Failed to load items: ${error.message}`);
      } finally {
        setLoading(false);
      }
    };

    fetchItems();
  }, []);

  if (error) {
    return (
      <div className="text-center py-8">
        <div className="text-red-500 mb-4">{error}</div>
        <button 
          onClick={() => {
            setLoading(true);
            setError(null);
            fetchItems();
          }}
          className="bg-blue-500 text-white px-4 py-2 rounded hover:bg-blue-600"
        >
          Retry
        </button>
      </div>
    );
  }

  if (loading) {
    return <div className="text-center py-8">Loading auction items...</div>;
  }

  return (
    <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
      {items.map(item => (
        <div key={item._id} className="bg-white rounded-lg shadow-md overflow-hidden">
          <img 
            src={item.image} 
            alt={item.title} 
            className="w-full h-48 object-cover"
          />
          <div className="p-4">
            <h3 className="text-xl font-semibold mb-2">{item.title}</h3>
            <p className="text-gray-600 mb-4">{item.description}</p>
            <div className="flex justify-between items-center">
              <span className="font-bold">${item.currentBid}</span>
              <div className="flex flex-col gap-2">
                <Link 
                  to={`/item/${item._id}`}
                  className="bg-gray-500 text-white px-4 py-2 rounded hover:bg-gray-600 text-center"
                >
                  View Details
                </Link>
                <div className="flex gap-2">
                  <input
                    type="number"
                    min={item.currentBid + 1}
                    placeholder={`Min $${item.currentBid + 1}`}
                    className="border p-2 rounded flex-grow"
                  />
                  <button 
                    className="bg-blue-500 text-white px-4 py-2 rounded hover:bg-blue-600"
                    onClick={() => handlePlaceBid(item._id, item.currentBid)}
                  >
                    Bid
                  </button>
                </div>
              </div>
            </div>
          </div>
        </div>
      ))}
    </div>
  );
}

export default Home;
