import React, { useState, useEffect } from 'react';
import api from '../services/api';

function BidForm({ itemId, currentBid, endTime, bidHistory }) {
  const [bidAmount, setBidAmount] = useState('');
  const [error, setError] = useState('');
  const [remainingTime, setRemainingTime] = useState('');

  // Calculate and update remaining time
  useEffect(() => {
    const timer = setInterval(() => {
      const now = new Date();
      const end = new Date(endTime);
      const timeLeft = end - now;

      if (timeLeft <= 0) {
        clearInterval(timer);
        setRemainingTime('Auction ended');
      } else {
        const days = Math.floor(timeLeft / (1000 * 60 * 60 * 24));
        const hours = Math.floor((timeLeft % (1000 * 60 * 60 * 24)) / (1000 * 60 * 60));
        const minutes = Math.floor((timeLeft % (1000 * 60 * 60)) / (1000 * 60));
        const seconds = Math.floor((timeLeft % (1000 * 60)) / 1000);

        setRemainingTime(
          `${days > 0 ? `${days}d ` : ''}${hours}h ${minutes}m ${seconds}s`
        );
      }
    }, 1000);

    return () => clearInterval(timer);
  }, [endTime]);

  const handleBidSubmit = async (e) => {
    e.preventDefault();
    const bidValue = parseFloat(bidAmount);

    if (isNaN(bidValue)) {
      setError('Please enter a valid bid amount');
      return;
    }

    if (bidValue <= currentBid) {
      setError(`Bid must be higher than $${currentBid}`);
      return;
    }

    try {
      const { data } = await api.placeBid(itemId, bidValue);
      alert(`Bid of $${bidValue} placed successfully!`);
      setBidAmount('');
      setError('');
      onBidSuccess(data);
    } catch (err) {
      console.error('Error placing bid:', err);
      setError(err.response?.data?.msg || 'Failed to place bid. Please try again.');
    }
  };

  return (
    <div className="mt-4 p-4 border rounded">
      <div className="mb-4">
        <h3 className="font-bold">Time Remaining:</h3>
        <p className="text-lg">{remainingTime}</p>
      </div>

      <form onSubmit={handleBidSubmit}>
        {error && <p className="text-red-500 mb-2">{error}</p>}
        <div className="flex items-center">
          <input
            type="number"
            value={bidAmount}
            onChange={(e) => setBidAmount(e.target.value)}
            placeholder={`Enter bid (min $${currentBid + 1})`}
            className="border p-2 rounded flex-grow"
            min={currentBid + 1}
            step="0.01"
            required
          />
          <button 
            type="submit" 
            className="bg-blue-500 hover:bg-blue-600 text-white px-4 py-2 rounded ml-2"
          >
            Place Bid
          </button>
        </div>
      </form>

      {bidHistory?.length > 0 && (
        <div className="mt-6">
          <h3 className="font-bold mb-2">Bid History</h3>
          <ul className="space-y-1">
            {bidHistory.map((bid, index) => (
              <li key={index} className="flex justify-between">
                <span>${bid.amount.toFixed(2)}</span>
                <span className="text-gray-500 text-sm">
                  {new Date(bid.timestamp).toLocaleString()}
                </span>
              </li>
            ))}
          </ul>
        </div>
      )}
    </div>
  );
}

export default BidForm;
